import React, {  Fragment } from "react";
import Header from '../components/Header';
import Leftnav from '../components/Leftnav';
import Pagetitle from '../components/Pagetitle';
import Appfooter from '../components/Appfooter';
import Popupchat from '../components/Popupchat';
import RequestEach from '../components/RequestEach'
import { useContext } from 'react';
import { ApiContext } from '../context/ApiContext';


const Request = () => {

  const { profiles, profile,  friendrequest} = useContext(
    ApiContext
  );
  const listProfiles =
    profile.id &&
    friendrequest.map(ask =>
      <RequestEach
        key={ask.id}
        ask = {ask}
        prof = {profiles.find(item=> {return item.userProfile === ask.askFrom})}
      />
    );
    return (
        <Fragment>

            <Header />
            <Leftnav />

            <div className="main-content right-chat-active">
                <div className="middle-sidebar-bottom">
                    <div className="middle-sidebar-left pe-0">
                        <div className="row">
                            <div className="col-xl-12">
                                <Pagetitle title="受け取り中のフレンド申請"/>
                                <div className="row ps-2 pe-2">
                                    {listProfiles}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            <Popupchat />
            <Appfooter /> 

        </Fragment>
    );

}


export default Request;
