import React, { Fragment, useState } from "react";
import Header from '../components/Header';
import Leftnav from '../components/Leftnav';
import Appfooter from '../components/Appfooter';
import RecommenduserEach from '../components/RecommenduserEach'
import { useContext } from 'react';
import { ApiContext } from '../context/ApiContext';

const Recommenduser = () => {
    const { profiles, profile} = useContext(
        ApiContext
      );

    const [inputValue, setInputValue] = useState()
    const [showprofiles, setShowprofiles] = useState()

    const handleInputChange = (e) => {
        setInputValue(e.target.value)
        search(e.target.value)
      }

    const search = (value) => {
        // 検索欄への入力が空の場合は早期return
        if (value === "") {
          setShowprofiles(profiles);
          return;
        }

        const serchedprofiles = profiles.filter(
          (user) =>

            Object.values(user.nickName).filter(
                (item) =>
                    item !== undefined &&
                    item !== null &&
                    item.toUpperCase().indexOf(value.toUpperCase()) !== -1
            ).length > 0
        );

        setShowprofiles(serchedprofiles);
      }

    const defaultProfiles =
    profile.id &&
    profiles &&
    profiles.map(profile =>
        <RecommenduserEach
        key={profile.nickName}
        profileeach = {profile}
        />
        )

    const listProfiles =
    profile.id &&
    showprofiles &&
    showprofiles.map(profile =>
        <RecommenduserEach
        key={profile.nickName}
        profileeach = {profile}
        />
        )

    console.log(listProfiles)


        return (
            <Fragment>

                <Header />
                <Leftnav />

                <div className="main-content right-chat-active">
                    <div className="middle-sidebar-bottom">
                        <div className="middle-sidebar-left pe-0">
                            <div className="row">
                                <div className="col-xl-12">


                                <div className='card shadow-xss w-100 d-block d-flex border-0 p-4 mb-3'>
                                    <h2 className='fw-700 mb-0 mt-0 font-md text-grey-900 d-flex align-items-center'>あなたに似たユーザー
                                    <form action="#" className='pt-0 pb-0 ms-auto'>
                                        <div className='search-form-2 ms-2'>
                                            <i className='ti-search font-xss'></i>
                                            <input type='text' value={inputValue} onChange={handleInputChange} className='form-control text-grey-500 mb-0 bg-greylight theme-dark-bg border-0' placeholder='ユーザーを探す'/>
                                        </div>
                                    </form>
                                    <a href='/' className='btn-round-md ms-2 bg-greylight theme-dark-bg rounded-3'><i className='feather-filter font-xss text-grey-500'></i></a>

                                    </h2>

                                </div>
                                    <div className="row ps-2 pe-2">
                                        {inputValue ?
                                        listProfiles:
                                        defaultProfiles}
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

                <Appfooter /> 

            </Fragment>
        );

}

export default Recommenduser;
