import React from 'react';
import { useContext } from 'react';
import { ApiContext } from '../context/ApiContext'
import { RequestleftEach } from '../components/RequestleftEach'


const Requestleft = () => {
    const { profiles, profile, friendrequest} = useContext(
        ApiContext
      );
    const listProfiles =
    profile.id &&
    friendrequest.map(ask =>
      <RequestleftEach
        key={ask.id}
        ask = {ask}
        prof = {profiles.find(item=> {return item.userProfile === ask.askFrom})}
      />
    );
    return (
        <div className='card w-100 shadow-xss rounded-xxl border-0 mb-3'>
            <div className='ard-body d-flex align-items-center p-4'>
                <h4 className='fw-700 mb-0 font-xssss text-grey-900'>フレンドリクエスト</h4>
                <a href='/request' className='fw-600 ms-auto font-xssss text-primary'>全て表示</a>
            </div>
            {listProfiles}


        </div>
    )

}

export default Requestleft ;
