import React from 'react';
import { useContext, useState } from 'react';
import { ApiContext } from '../context/ApiContext'
import Lightbox from 'react-image-lightbox';

const SpecificUserpageEach = () => {
    const {profile,  newRequestFriend, intuser } = useContext(ApiContext)
    const [isOpenModal, setIsOpenModal] = useState(false);



    const createNewRequest = () => {
        const uploadDataAsk = new FormData();
        uploadDataAsk.append("askTo",profile.userProfile)
        uploadDataAsk.append("approved", false);
        newRequestFriend(uploadDataAsk);
      };

    return (
        <div className='card w-100 border-0  bg-white shadow-xss rounded-xxl'>
            <div className="card-body d-block  p-3">
                {intuser.imgBackground ?
                    <div className="row ps-2 pe-2">
                        <div className="col-sm-12 p-1"><img src={intuser.imgBackground} className="rounded-xxl w-100" alt="post" /></div>                                        
                    </div>
                    : <div className="row ps-2 pe-2">
                    <div className="col-sm-12 p-1"><img src="http://127.0.0.1:8000/media/image/default_background.jpg" className="rounded-xxl w-100" alt="post" /></div>                                        
                </div>}
            </div>
            <div className='card-body p-0 position-relative'>
                { intuser.id ?
                // <figure className='avatar position-absolute w100 z-index-1' style={{top:'-40px',left:'30px'}}><img src={intuser.img} alt="avater" className='float-right p-1 bg-white rounded-circle w-100'/></figure>
                <div>
                {isOpenModal && (
                    <Lightbox
                        mainSrc={intuser.img}
                        onCloseRequest={() => setIsOpenModal(false)}
                    />
                )}
                    <div onClick={() => setIsOpenModal(true)}>
                    <figure className='avatar position-absolute w100 z-index-1' style={{top:'-40px',left:'30px'}}>
                    <img src={intuser.img} alt="avater" className='float-right p-1 bg-white rounded-circle w-100'/>
                    </figure>
                </div>
                </div>
                :
                <figure className='avatar position-absolute w100 z-index-1' style={{top:'-40px',left:'30px'}}><img src="http://127.0.0.1:8000/media/image/サラリーマン.img" alt="avater" className='float-right p-1 bg-white rounded-circle w-100'/></figure>}
                <h4 className='fw-700 font-sm mt-2 mb-lg-5 mb-4 pl-15'>{intuser.nickName}<span className='fw-500 font-xssss text-grey-500 mt-1 mb-3 d-block'>{intuser.created_on}</span></h4>
                <div className='d-flex align-items-center justify-content-center position-absolute-md right-15 top-0 me-2'>
                    <a className='d-none d-lg-block bg-primary-gradiant me-2 p-3 z-index-1 rounded-3 text-white font-xsssss text-uppercase fw-700 ls-3'onClick={() => createNewRequest()}>フォロー</a>
                </div>

            </div>

            <div className='card-body d-block w-100 shadow-none mb-0 p-0 border-top-xs'>
                <ul className='nav nav-tabs h55 d-flex product-info-tab border-bottom-0 ps-4' id="pills-tab" role="tablist" >
                    <li className='active list-inline-item me-5'><a className='fw-700 font-xssss text-grey-500 pt-3 pb-3 ls-1 d-inline-block active' href="#navtabs1" data-toggle="tab">投稿</a></li>
                    {/* <li className="list-inline-item me-5"><a className="fw-700 font-xssss text-grey-500 pt-3 pb-3 ls-1 d-inline-block" href="#navtabs2" data-toggle="tab">モノ</a></li>
                    <li className="list-inline-item me-5"><a className="fw-700 font-xssss text-grey-500 pt-3 pb-3 ls-1 d-inline-block" href="#navtabs3" data-toggle="tab">コト</a></li>
                    <li className="list-inline-item me-5"><a className="fw-700 font-xssss text-grey-500 pt-3 pb-3 ls-1 d-inline-block" href="#navtabs4" data-toggle="tab">メディア</a></li> */}

                </ul>

            </div>

        </div>
    )

}

export default SpecificUserpageEach;
