import React, {useContext} from 'react'
import { ApiContext } from '../context/ApiContext'
import { useHistory } from "react-router-dom";


const RequestEach = ({ ask, prof}) => {
    const { profile, newUserIntUser, getUserInterest, changeApprovalRequest,getFriendRequest } = useContext(ApiContext);
    const history = useHistory()

    const changeApproval = () => {
        const uploadDataAsk = new FormData();
        uploadDataAsk.append("askTo", ask.askTo);
        uploadDataAsk.append("approved", true);
        changeApprovalRequest(uploadDataAsk, ask);
        getFriendRequest()
      };

    const toSpecificUserPage = () => {
    if (profile.userProfile === prof.userProfile) {
        console.log("本人")
        history.push("/userpage")

    } else {
        const createdIntData = new FormData()
        createdIntData.append("intUserId", prof.userProfile)
        newUserIntUser(createdIntData)
        console.log("ちがう")
        getUserInterest()
        history.push("/specificuserpage")}
    }




    return (
            <div className="col-md-3 col-sm-4 pe-2 ps-2">
                <div className="card d-block border-0 shadow-xss rounded-3 overflow-hidden mb-3">
                    <div className="card-body d-block w-100 ps-3 pe-3 pb-4 text-center">
                        {prof.img ? (
                        <figure className="overflow-hidden avatar ms-auto me-auto mb-0 position-relative w65 z-index-1 pointer" onClick={() => toSpecificUserPage()}><img src={prof.img} alt="avater" className="float-right p-0 bg-white rounded-circle w-100 shadow-xss" /></figure>): (
                        <figure className="overflow-hidden avatar ms-auto me-auto mb-0 position-relative w65 z-index-1"><img src="http://127.0.0.1:8000/media/image/サラリーマン.img" alt="avater" className="float-right p-0 bg-white rounded-circle w-100 shadow-xss" /></figure>)}
                        <div className="clearfix w-100"></div>
                        <h4 className="fw-700 font-xsss mt-3 mb-0">{prof.nickName} </h4>
                        <p className="fw-500 font-xssss text-grey-500 mt-0 mb-3">{prof.created_on}</p>
                        {!ask.approved ? (
                        <button href="" className="mt-0 btn pt-2 pb-2 ps-3 pe-3 lh-24 ms-1 ls-3 d-inline-block rounded-xl bg-success font-xsssss fw-700 ls-lg text-white" onClick={() => changeApproval()}>承認</button>):(
                        <button href="" className="mt-0 btn pt-2 pb-2 ps-3 pe-3 lh-24 ms-1 ls-3 d-inline-block rounded-xl bg-success font-xsssss fw-700 ls-lg text-white" disabled>承認</button>)}
                    </div>
                </div>
            </div> 
    )
}

export default RequestEach
